import "./App.css";
import React, { useState, useEffect } from "react";
import Axions from "axios";

function App() {
  const [memberList, setMemberList] = useState([]);

  useEffect(() => {
    Axions.get("http://localhost:3001/api/getAllMembers").then((response) => {
      setMemberList(response.data);
    });
  }, []);
  const handleUpdate = async (id) => {
    // code to update a member goes here
  };

  // code to delete a member goes here
  const handleDelete = (memid) => {
    Axions.delete(`http://localhost:3001/api/deleteMemberID/${memid}`);
  };
  return (
    <div className="App">
      <header className="App-header">
        <h1>Gym Management System</h1>
      </header>
      <div className="btn-grp">
        <button>Members</button>
        <button>Trainers</button>
      </div>
      <div className="tables">
        <table>
          <tr>
            <th>Name</th>
            <th>Address</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
          {memberList.map((value) => {
            return (
              <tbody>
                <tr key={value.MemberID}>
                  <td>{value.Name}</td>
                  <td>{value.Address}</td>
                  <td>{value.Phone}</td>
                  <td>{value.Email}</td>
                  <td>
                    <div id="action-btn">
                      <button
                        onClick={() => {
                          handleUpdate(value.MemberID);
                        }}
                      >
                        Update
                      </button>
                      <button
                        onClick={() => {
                          handleDelete(value.MemberID);
                        }}
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              </tbody>
            );
          })}
        </table>
      </div>
    </div>
  );
}

export default App;
