import "./App.css";
import React, { useState, useEffect } from "react";
import Axions from "axios";
import UpdateMember from "./UpdateMember";

function App() {
  const [memberList, setMemberList] = useState([]);

  useEffect(() => {
    Axions.get("http://localhost:3001/api/getAllMembers").then((response) => {
      setMemberList(response.data);
    });
  }, []);

  const [showUpdateModal, setShowUpdateModal] = useState(false);
  const [selectedMemberId, setSelectedMemberId] = useState(null);

  const handleUpdate = (memberId) => {
    setSelectedMemberId(memberId);
    setShowUpdateModal(true);
  };
  // code to delete a member goes here
  const handleDelete = (memid) => {
    Axions.delete(`http://localhost:3001/api/deleteMemberID/${memid}`);
  };
  return (
    <div className="App">
      <header className="App-header">
        <h1>Gym Management System</h1>
      </header>
      <div className="btn-grp">
        <button>Members</button>
        <button>Trainers</button>
      </div>
      <div className="tables">
        {showUpdateModal && (
          <UpdateMember
            memberId={selectedMemberId}
            onClose={() => setShowUpdateModal(false)}
          />
        )}
        <table>
          <tr>
            <th>Name</th>
            <th>Address</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
          {memberList.map((value) => {
            return (
              <tbody>
                <tr key={value.MemberID}>
                  <td>{value.Name}</td>
                  <td>{value.Address}</td>
                  <td>{value.Phone}</td>
                  <td>{value.Email}</td>
                  <td>
                    <div id="action-btn">
                      <button
                        onClick={() => {
                          handleUpdate(value.MemberID);
                        }}
                      >
                        Update
                      </button>
                      <button
                        onClick={() => {
                          if (
                            window.confirm(
                              "Are you sure you want to delete the member?"
                            )
                          ) {
                            handleDelete(value.MemberID);
                          }
                        }}
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              </tbody>
            );
          })}
        </table>
      </div>
    </div>
  );
}

export default App;
