import React, { useState } from "react";
import "./UpdateMember.css";

const UpdateMember = ({ member, handleUpdate }) => {
  // state to hold the updated member details
  const [updatedMember, setUpdatedMember] = useState({
    Name: member.Name,
    Address: member.Address,
    Phone: member.Phone,
    Email: member.Email,
  });

  // function to handle changes in the form input fields
  const handleChange = (event) => {
    Axions.put("http://localhost:3001/api/updateMemberID", {
      Name: member.Name,
      Address: member.Address,
      Phone: member.Phone,
      Email: member.Email,
    }).then(() => {
      alert("succesful insert!");
    });
    setUpdatedMember({
      ...updatedMember,
      [event.target.name]: event.target.value,
    });
  };

  // function to submit the updated member details
  const handleSubmit = (event) => {
    event.preventDefault();
    handleUpdate(updatedMember, member.MemberID);
  };

  return (
    <div className="update-form-container">
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="Name">Name</label>
          <input
            type="text"
            id="Name"
            name="Name"
            value={updatedMember.Name}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="Address">Address</label>
          <input
            type="text"
            id="Address"
            name="Address"
            value={updatedMember.Address}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="Phone">Phone</label>
          <input
            type="text"
            id="Phone"
            name="Phone"
            value={updatedMember.Phone}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="Email">Email</label>
          <input
            type="email"
            id="Email"
            name="Email"
            value={updatedMember.Email}
            onChange={handleChange}
            required
          />
        </div>
        <div className="form-group">
          <button type="submit">Update</button>
        </div>
      </form>
    </div>
  );
};

export default UpdateMember;
