import React, { useState } from "react";
import axios from "axios";

const UpdateMember = ({ member, handleUpdate }) => {
  // state to hold the updated member data

  const [updatedMember, setUpdatedMember] = useState({
    Name: member?.Name || "",
    Address: member?.Address || "",
    Phone: member?.Phone || "",
    Email: member.Email || "",
  });

  // function to handle form inputs change
  const handleChange = (event) => {
    setUpdatedMember({
      ...updatedMember,
      [event.target.name]: event.target.value,
    });
  };

  // function to handle form submission
  const handleSubmit = (event) => {
    event.preventDefault();
    axios
      .put("http://localhost:3001/api/updateMemberID", {
        Name: updatedMember.Name,
        Address: updatedMember.Address,
        Phone: updatedMember.Phone,
        Email: updatedMember.Email,
      })
      .then(() => {
        alert("Update Successful!");
        handleUpdate(updatedMember, member.MemberID);
      })
      .catch((error) => {
        console.error(error);
      });
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Name:
            <input
              type="text"
              name="Name"
              value={updatedMember.Name}
              onChange={handleChange}
            />
          </label>
        </div>
        <div>
          <label>
            Address:
            <input
              type="text"
              name="Address"
              value={updatedMember.Address}
              onChange={handleChange}
            />
          </label>
        </div>
        <div>
          <label>
            Phone:
            <input
              type="text"
              name="Phone"
              value={updatedMember.Phone}
              onChange={handleChange}
            />
          </label>
        </div>
        <div>
          <label>
            Email:
            <input
              type="email"
              name="Email"
              value={updatedMember.Email}
              onChange={handleChange}
            />
          </label>
        </div>
        <button type="submit">Update</button>
      </form>
    </div>
  );
};

export default UpdateMember;
