import "./App.css";
import React, { useState, useEffect } from "react";
import Axions from "axios";
import UpdateMember from "./UpdateMember";
import TrainerTable from "./TrainerTable";
import PaymentTable from "./PaymentTable";

function App() {
  const [memberList, setMemberList] = useState([]);
  const [trainers, setTrainers] = useState([]);
  const [payments, setPayments] = useState([]);
  const [showPayments, setShowPayments] = useState(false);
  const [showTrainers, setShowTrainers] = useState(false);

  useEffect(() => {
    Axions.get("http://localhost:3001/api/getAllMembers").then((response) => {
      setMemberList(response.data);
    });
  }, []);

  useEffect(() => {
    Axions.get("http://localhost:3001/api/getAllTrainers").then((response) => {
      setTrainers(response.data);
    });
  }, []);

  useEffect(() => {
    Axions.get("http://localhost:3001/api/getPayments").then((response) => {
      setPayments(response.data);
    });
  }, []);

  const [showUpdate, setShowUpdate] = useState(false);
  const [selectedId, setSelectedId] = useState(null);

  const handleUpdate = (id) => {
    setShowUpdate(true);
    setSelectedId(id);
  };

  // code to delete a member goes here
  const handleDelete = (memid) => {
    Axions.delete(`http://localhost:3001/api/deleteMemberID/${memid}`);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Gym Management System</h1>
      </header>
      <div className="btn-grp">
        <button>Members</button>
        <button onClick={() => setShowTrainers(!showTrainers)}>Trainers</button>
        <button onClick={() => setShowPayments(!showPayments)}>Payments</button>
      </div>
      <div className="tables">
        {showUpdate && (
          <UpdateMember
            memberId={selectedId}
            closeModal={() => setShowUpdate(false)}
          />
        )}
        <h2>Members</h2>
        <table>
          <tr>
            <th>Name</th>
            <th>Address</th>
            <th>Phone</th>
            <th>Email</th>
            <th>Actions</th>
          </tr>
          {memberList.map((value) => {
            return (
              <tbody>
                <tr key={value.MemberID}>
                  <td>{value.Name}</td>
                  <td>{value.Address}</td>
                  <td>{value.Phone}</td>
                  <td>{value.Email}</td>
                  <td>
                    <div id="action-btn">
                      <button
                        onClick={() => {
                          handleUpdate(value.MemberID);
                        }}
                      >
                        Update
                      </button>
                      <button
                        onClick={() => {
                          if (
                            window.confirm(
                              "Are you sure you want to delete the member?"
                            )
                          ) {
                            handleDelete(value.MemberID);
                          }
                        }}
                      >
                        Delete
                      </button>
                    </div>
                  </td>
                </tr>
              </tbody>
            );
          })}
        </table>
        {showTrainers && (
          <div>
            <h2>Trainers</h2>
            <TrainerTable trainer={trainers} />
          </div>
        )}
        {showPayments && (
          <div>
            <h2>Payments</h2>
            <PaymentTable payments={payments} />
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
