import React, { useState } from "react";

const UpdateMember = ({ member, handleUpdate }) => {
  const [updatedName, setUpdatedName] = useState("");
  const [updatedAddress, setUpdatedAddress] = useState("");
  const [updatedPhone, setUpdatedPhone] = useState("");
  const [updatedEmail, setUpdatedEmail] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    handleUpdate({
      MemberID: member.MemberID,
      Name: updatedName || member.Name,
      Address: updatedAddress || member.Address,
      Phone: updatedPhone || member.Phone,
      Email: updatedEmail || member.Email,
    });
  };

  return (
    <div className="update-member-container">
      <form className="update-member-form" onSubmit={handleSubmit}>
        <label htmlFor="name">
          Name:
          <input
            type="text"
            id="name"
            value={updatedName}
            onChange={(e) => setUpdatedName(e.target.value)}
          />
        </label>
        <label htmlFor="address">
          Address:
          <input
            type="text"
            id="address"
            value={updatedAddress}
            onChange={(e) => setUpdatedAddress(e.target.value)}
          />
        </label>
        <label htmlFor="phone">
          Phone:
          <input
            type="text"
            id="phone"
            value={updatedPhone}
            onChange={(e) => setUpdatedPhone(e.target.value)}
          />
        </label>
        <label htmlFor="email">
          Email:
          <input
            type="email"
            id="email"
            value={updatedEmail}
            onChange={(e) => setUpdatedEmail(e.target.value)}
          />
        </label>
        <button className="update-member-submit" type="submit">
          Update
        </button>
      </form>
    </div>
  );
};

export default UpdateMember;
