import React, { useState } from "react";

const UpdateMember = ({ value, handleUpdate }) => {
  const [formData, setFormData] = useState({
    Name: value.Name,
    Address: value.Address,
    Phone: value.Phone,
    Email: value.Email,
  });

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <form>
      <input
        type="text"
        name="Name"
        value={formData.Name}
        onChange={handleInputChange}
      />
      <input
        type="text"
        name="Address"
        value={formData.Address}
        onChange={handleInputChange}
      />
      <input
        type="text"
        name="Phone"
        value={formData.Phone}
        onChange={handleInputChange}
      />
      <input
        type="email"
        name="Email"
        value={formData.Email}
        onChange={handleInputChange}
      />
      <button
        type="button"
        onClick={() => handleUpdate(value.MemberID, formData)}
      >
        Update
      </button>
    </form>
  );
};

export default UpdateMember;
